package logic;

public class Position {
	protected int x;
	protected int y;
	
	public Position(int x, int y) {
		super();
		this.x = x;
		this.y = y;
	}

	public int getX() {
		return  x;
	}

	public void setX(int d) {
		this.x = d;
	}

	public int getY() {
		return y;
	}

	public void setY(int d) {
		// TODO Auto-generated method stub
		this.y = d;
	}
}
